import React, { useEffect, useState } from "react";

const ReviewedItemsContext = React.createContext({
    reviewedItems: [], fetchReviewedItems: () => { }
});

export async function GetReviewedItems() {
    const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
    const reviewedItems = await response.json()
    console.log(reviewedItems)
    const divi = document.querySelector('#valorados')
    divi.replaceChildren()
    return (

        reviewedItems.map(
            (item) => {
                // const DOM_img = document.createElement("img");
                // DOM_img.src = item;
                // DOM_img.className = 'img-thumbnail';
                // DOM_img.width = 200
                // DOM_img.height = 200
                // divi.appendChild(DOM_img)
                const div1 = document.createElement("div");
                div1.class = "col-md-4"

                const div2 = document.createElement("div");
                div2.class = "card card-span h-100 text-white"

                

                // <div class="col-md-4">
                //     <div class="card card-span h-100 text-white"><img class="card-img h-100" src="assets/img/gallery/vanity-bag.png" alt="..." />
                //         <div class="card-img-overlay bg-dark-gradient">
                //             <div class="d-flex align-items-end justify-content-center h-100">
                //                 <a class="btn btn-lg text-light fs-1" href="#!" role="button">Vanity Bags</a>
                //             </div>
                //         </div>
                //     </div>
                // </div>
                divi.appendChild(document.createTextNode(reviewedItems))
            }
        )
    );

}

export default function ReviewedItems() {
    const [reviewedItems, setReviewedItems] = useState([])
    const fetchReviewedItems = async () => {
        const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
        const reviewedItems = await response.json()
        setReviewedItems(reviewedItems)
        console.log(reviewedItems)
    }
    useEffect(() => {
        fetchReviewedItems()
    }, [])
    return (
        <ReviewedItemsContext.Provider value={{ reviewedItems, fetchReviewedItems }}>

            {
                reviewedItems.map(
                    (item, index) => {

                        <div class="col-md-12">
                            <div class="card card-span h-100 text-white"><img id={JSON.stringify("img-" + index)} class="card-img h-100" src="assets/img/gallery/vanity-bag.png" alt="..."></img>
                                <div class="card-img-overlay bg-dark-gradient">
                                    <div class="d-flex align-items-end justify-content-center h-100"><a class="btn btn-lg text-light fs-1" href="#!" role="button">Vanity Bags</a></div>
                                </div>
                            </div>
                        </div>


                    }
                )
            }
            {reviewedItems}
        </ReviewedItemsContext.Provider>
    )
}
