import React, { useEffect, useState } from "react";

const ReviewedItemsContext = React.createContext({
    reviewedItems: [], fetchReviewedItems: () => { }
});

export async function GetReviewedItems() {
    const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
    const reviewedItems = await response.json()
    console.log(reviewedItems)
    return (
        reviewedItems.map(
            (item) => {
                // const DOM_img = document.createElement("img");
                // DOM_img.src = item;
                // DOM_img.className = 'img-thumbnail';
                // DOM_img.width = 200
                // DOM_img.height = 200
                // divi.appendChild(DOM_img)
                <div class="col-sm-6 col-md-3 mb-3 mb-md-0 h-100">
                    <div class="card card-span h-100 text-white"><img src={item} class="img-fluid h-100"
                        alt='...' />
                        <div class="card-img-overlay ps-0"> </div>
                        <div class="card-body ps-0 bg-200">
                            <h5 class="fw-bold text-1000 text-truncate">Flat Hill Slingback</h5>
                            <div class="fw-bold"><span class="text-600 me-2 text-decoration-line-through">$200</span><span class="text-primary">$175</span></div>
                        </div><a class="stretched-link" href="#"></a>
                    </div>
                </div>
            }
        )
    );

}

export default function ReviewedItems() {
    const [reviewedItems, setReviewedItems] = useState([])
    const fetchReviewedItems = async () => {
        const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
        const reviewedItems = await response.json()
        setReviewedItems(reviewedItems)
        console.log(reviewedItems)
    }
    useEffect(() => {
        fetchReviewedItems()
    }, [])
    return (
        <ReviewedItemsContext.Provider value={{ reviewedItems, fetchReviewedItems }}>
            {
                reviewedItems.map(
                    item => {
                        <div class="col-sm-6 col-md-3 mb-3 mb-md-0 h-100">
                            <div class="card card-span h-100 text-white"><img src={item} class="img-fluid h-100"
                                alt='...' />
                                <div class="card-img-overlay ps-0"> </div>
                                <div class="card-body ps-0 bg-200">
                                    <h5 class="fw-bold text-1000 text-truncate">Flat Hill Slingback</h5>
                                    <div class="fw-bold"><span class="text-600 me-2 text-decoration-line-through">$200</span><span class="text-primary">$175</span></div>
                                </div><a class="stretched-link" href="#"></a>
                            </div>
                        </div>



                    }
                )
            }
        </ReviewedItemsContext.Provider>
    )
}
