import React, { useEffect, useState } from "react";

const ReviewedItemsContext = React.createContext({
    reviewedItems: [], fetchReviewedItems: () => { }
});

export async function GetReviewedItems() {
    const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
    const reviewedItems = await response.json()
    console.log(reviewedItems)
    const divi = document.querySelector('#valorados')
    divi.replaceChildren()
    return (
        
        reviewedItems.map(
            (item) => {
                // const DOM_img = document.createElement("img");
                // DOM_img.src = item;
                // DOM_img.className = 'img-thumbnail';
                // DOM_img.width = 200
                // DOM_img.height = 200
                // divi.appendChild(DOM_img)
                
                // <div class="col-md-4">
                //     <div class="card card-span h-100 text-white"><img class="card-img h-100" src="assets/img/gallery/vanity-bag.png" alt="..." />
                //         <div class="card-img-overlay bg-dark-gradient">
                //             <div class="d-flex align-items-end justify-content-center h-100">
                // <a class="btn btn-lg text-light fs-1" href="#!" role="button">Vanity Bags<svg class="bi bi-arrow-right-short" xmlns="http://www.w3.org/2000/svg" width="23" height="23" fill="currentColor" viewBox="0 0 16 16">
                //                 <path fill-rule="evenodd" d="M4 8a.5.5 0 0 1 .5-.5h5.793L8.146 5.354a.5.5 0 1 1 .708-.708l3 3a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708-.708L10.293 8.5H4.5A.5.5 0 0 1 4 8z"> </path>
                //             </svg></a></div>
                //         </div>
                //     </div>
                // </div>
                divi.appendChild(document.createTextNode(reviewedItems))
            }
        )
    );

}

export default function ReviewedItems() {
    const [reviewedItems, setReviewedItems] = useState([])
    const fetchReviewedItems = async () => {
        const response = await fetch("http://localhost:8000/reviewedItems/" + window.$user)
        const reviewedItems = await response.json()
        setReviewedItems(reviewedItems)
        console.log(reviewedItems)
    }
    useEffect(() => {
        fetchReviewedItems()
    }, [])
    return (
        <ReviewedItemsContext.Provider value={{ reviewedItems, fetchReviewedItems }}>
            
            {
                reviewedItems.map(
                    (item, index) => {

                        <div class="col-md-12">
                            <div class="card card-span h-100 text-white"><img id={JSON.stringify("")} class="card-img h-100" src="assets/img/gallery/vanity-bag.png" alt="..."></img>
                                <div class="card-img-overlay bg-dark-gradient">
                                    <div class="d-flex align-items-end justify-content-center h-100"><a class="btn btn-lg text-light fs-1" href="#!" role="button">Vanity Bags<svg class="bi bi-arrow-right-short" xmlns="http://www.w3.org/2000/svg" width="23" height="23" fill="currentColor" viewBox="0 0 16 16">
                                        <path fill-rule="evenodd" d="M4 8a.5.5 0 0 1 .5-.5h5.793L8.146 5.354a.5.5 0 1 1 .708-.708l3 3a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708-.708L10.293 8.5H4.5A.5.5 0 0 1 4 8z"> </path>
                                    </svg></a></div>
                                </div>
                            </div>
                        </div>


                    }
                )
            }
            {reviewedItems}
        </ReviewedItemsContext.Provider>
    )
}
