import React, { useEffect, useState } from "react";

const RecommedationsContext = React.createContext({
    todos: [], fetchTodos: () => { }
});

export async function GetRecommendations() {
    const response = await fetch("http://localhost:8000/recommendation/vgg/" + window.$user)
    const todos = await response.json()
    console.log(todos)
    const divi = document.querySelector('#recomendaciones')
    divi.replaceChildren()
    return (


        Object.entries(todos).map(([key, value]) => {
            const div1 = document.createElement("div");
            div1.setAttribute("class", "col-sm-6 col-md-3 mb-3 mb-md-0 h-100")

            const div2 = document.createElement("div");
            div2.setAttribute("class", "card card-span h-100 text-white")

            const img = document.createElement("img");
            img.src = value;
            img.setAttribute("class", "card-img h-100")

            const div3 = document.createElement("div");
            div3.setAttribute("class", "card-img-overlay bg-dark-gradient d-flex flex-column-reverse")

            const title = document.createElement("p");
            p.innerHTML = key;
            title.setAttribute("class", "text-400 fs-1")


            const a = document.createElement("a");
            a.setAttribute("class", "stretched-link")

            div3.appendChild(p)
            div2.appendChild(img)
            div2.appendChild(div3)
            div2.appendChild(a)
            div1.appendChild(div2)
            divi.appendChild(div1)
        })


    )
}

export default function Recommendations() {
    const [todos, setRecommedations] = useState([])
    const fetchRecommedations = async () => {
        const response = await fetch("http://localhost:8000/recommendation/vgg/" + window.$user)
        const todos = await response.json()
        setRecommedations(todos)
        console.log(todos)
    }
    const divi = document.querySelector('#recomendaciones')
    useEffect(() => {
        fetchRecommedations()
    }, [])
    return (
        <RecommedationsContext.Provider value={{ todos, fetchRecommedations }}>
            <div class="carousel-item active" data-bs-interval="10000">
                <div class="row h-100 align-items-center g-2">
                    {
                        Object.entries(todos).map(([key, value]) => {
                            divi.replaceChildren()
                            const div1 = document.createElement("div");
                            div1.setAttribute("class", "col-sm-6 col-md-3 mb-3 mb-md-0 h-100")

                            const div2 = document.createElement("div");
                            div2.setAttribute("class", "card card-span h-100 text-white")

                            const img = document.createElement("img");
                            img.src = value;
                            img.setAttribute("class", "card-img h-100")

                            const div3 = document.createElement("div");
                            div3.setAttribute("class", "card-img-overlay bg-dark-gradient d-flex flex-column-reverse")

                            const p = document.createElement("p");
                            p.innerHTML = key;
                            p.setAttribute("class", "text-400 fs-1")


                            const a = document.createElement("a");
                            a.setAttribute("class", "stretched-link")

                            div3.appendChild(p)
                            div2.appendChild(img)
                            div2.appendChild(div3)
                            div2.appendChild(a)
                            div1.appendChild(div2)
                            divi.appendChild(div1)

                            // <div class="col-sm-6 col-md-3 mb-3 mb-md-0 h-100">
                            //     <div class="card card-span h-100 text-white">
                            //         <img class="card-img h-100" src="assets/img/gallery/full-body.png" alt="..." />
                            //         <div class="card-img-overlay bg-dark-gradient d-flex flex-column-reverse">
                            //             <h6 class="text-primary">$175</h6>
                            //             <p class="text-400 fs-1">Jumper set for Women</p>
                            //             <h4 class="text-light">Flat Hill Slingback</h4>
                            //         </div>
                            //         <a class="stretched-link" href="#"></a>
                            //     </div>
                            // </div>

                            //   <p key={key}> this is my key {key} and this is my value {value}</p>
                        })
                    }
                </div>
            </div>
        </RecommedationsContext.Provider>
    )
}
